    "unit_order_refund_factor": 0.5,
    "FarmMouseOverTemplate": "<h4><%= name %></h4>\n<span class=\"small\"><%= player_relation_message %></span>\n<div class=\"popup_table_inside small\">\n    <div class=\"clearfix\">\n        <div class=\"fp_icon fp_mood\"></div>\n        <div class=\"fp_property\">\n            <div>Stimmung:</div>\n            <div class=\"farm_popup_progress_wrapper\">\n                <div class=\"fp_progress\" style=\"width:<%= mood %>%\"></div>\n                <div class=\"fp_text\"><%= mood %>%</div>\n            </div>\n        </div>\n    </div>\n    <div class=\"clearfix\">\n        <div class=\"fp_icon fp_exp\"></div>\n        <div class=\"fp_property\">\n            <div>Ausbaustufe: <%= expansion_stage %></div>\n            <div class=\"farm_popup_progress_wrapper expansion\">\n                <div class=\"fp_expansion\" style=\"width:<%= expansion_stage/6*100 %>%\"></div>\n            </div>\n        </div>\n    </div>\n    <div class=\"clearfix\">\n        <div class=\"fp_icon fp_trade\"></div>\n        <div class=\"fp_property\">\n            <div>Handel:</div>\n            <span class=\"you_pay resource_<%= demand %>_icon\"></span>\n            <span class=\"popup_ratio\">1:<%= Math.round(ratio * 100) / 100 %></span>\n            <span class=\"you_get resource_<%= offer %>_icon\"></span>\n        </div>\n    </div>\n\n    <% if(loot > Timestamp.now() && Game.gfx_level > 0 ){ %>\n    <div class=\"clearfix\">\n        <div class=\"fp_icon fp_loot\"></div>\n        <div class=\"fp_property\">\n            Nächste Forderung:<br/>\n            <%= DateHelper.timestampToLocaleTime(loot) %>\n        </div>\n    </div>\n</div>\n<% } %>\n</div>\n",
    "ConfirmDialogTemplate": "<div class=\"main_dialog_text_area\">\n\t<p class=\"confirm_dialog_text\">\n\t\t<%= content %>\n\t</p>\n\t<div class=\"dialog_buttons\">\n\t<%= confirm ? button(confirm, {href: '#confirm', onclick: onConfirm}, true) : '' %>\n\t<%= cancel ? button(cancel, {href: '#cancel'}, true) : '' %>\n\n\t<% if (check) { %>\n\t\t<hr />\n\t\t<% if (use_player_hint) { %>\n\t\t\t<a href=\"#player_hint_checkbox\" class=\"checkbox\"></a><span><%= check %></span>\n\t\t<% } else { %>\n\t\t\t<a href=\"#checkbox\" class=\"checkbox\"></a><span><%= check %></span>\n\t\t<% } %>\n\t<% } %>\n\t</div>\n</div>\n",
